mapper.update:
update fws set
name = #{name,jdbcType=VARCHAR}, age = #{age,jdbcType=VARCHAR}, sex = #{sex,jdbcType=VARCHAR}, gjy = #{gjy,jdbcType=VARCHAR}, xdz = #{xdz,jdbcType=VARCHAR}, phone = #{phone,jdbcType=VARCHAR}, jiguan = #{jiguan,jdbcType=VARCHAR}, birthday = #{birthday,jdbcType=VARCHAR}, mz = #{mz,jdbcType=VARCHAR}, sx = #{sx,jdbcType=VARCHAR}, yuyan = #{yuyan,jdbcType=VARCHAR}, shengao = #{shengao,jdbcType=VARCHAR}, tizhong = #{tizhong,jdbcType=VARCHAR}, xl = #{xl,jdbcType=VARCHAR}, zs = #{zs,jdbcType=VARCHAR}, lx = #{lx,jdbcType=VARCHAR}, state = #{state,jdbcType=VARCHAR}, money = #{money,jdbcType=VARCHAR}, sfzh = #{sfzh,jdbcType=VARCHAR}, sfzdz = #{sfzdz,jdbcType=VARCHAR}, gzlx = #{gzlx,jdbcType=VARCHAR}, des = #{des,jdbcType=VARCHAR}, techang = #{techang,jdbcType=VARCHAR}, xgtd = #{xgtd,jdbcType=VARCHAR}, remark = #{remark,jdbcType=VARCHAR} where
tid = #{tid}

