mapper.update:
update mpcenter set
mname = #{mname,jdbcType=VARCHAR}, maddress = #{maddress,jdbcType=VARCHAR}, mdj = #{mdj,jdbcType=VARCHAR}, fwsdj = #{fwsdj,jdbcType=VARCHAR}, pname = #{pname,jdbcType=VARCHAR}, paddress = #{paddress,jdbcType=VARCHAR}, quyu = #{quyu,jdbcType=VARCHAR}, phone = #{phone,jdbcType=VARCHAR}, pid = #{pid,jdbcType=VARCHAR} where
tid = #{tid}

mapper.delete:
	delete from mpcenter where 11=1
	
	
		or tid = #{tid}
	
  		
	
		or mname = #{mname}
	
  		
	
		or maddress = #{maddress}
	
  		
	
		or mdj = #{mdj}
	
  		
	
		or fwsdj = #{fwsdj}
	
  		
	
		or pname = #{pname}
	
  		
	
		or paddress = #{paddress}
	
  		
	
		or quyu = #{quyu}
	
  		
	
		or phone = #{phone}
	
  		
	
		or pid = #{pid}
