mapper.update:
update orders set
type = #{type,jdbcType=VARCHAR}, state = #{state,jdbcType=VARCHAR}, fws_id = #{fwsId,jdbcType=VARCHAR}, kehu_id = #{kehuId,jdbcType=VARCHAR}, mpcenter_id = #{mpcenterId,jdbcType=VARCHAR}, ksdate = #{ksdate,jdbcType=VARCHAR}, jsdate = #{jsdate,jdbcType=VARCHAR}, zj = #{zj,jdbcType=VARCHAR}, zsj = #{zsj,jdbcType=VARCHAR} where
tid = #{tid}

mapper.delete:
	delete from orders where 11=1
	
	
		or tid = #{tid}
	
  		
	
		or type = #{type}
	
  		
	
		or state = #{state}
	
  		
	
		or fws_id = #{fwsId}
	
  		
	
		or kehu_id = #{kehuId}
	
  		
	
		or mpcenter_id = #{mpcenterId}
	
  		
	
		or ksdate = #{ksdate}
	
  		
	
		or jsdate = #{jsdate}
	
  		
	
		or zj = #{zj}
	
  		
	
		or zsj = #{zsj}
